    private void btnCareerActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCareerActionPerformed
    
        backEnd = new BackEnd();
        ResultSet rs = null;
       
        try
        {
           rs = backEnd.getStudents(txtCareerName.getText());
           showStudentsTable(rs);
     
        }catch(HeadlessException e)         {
             System.out.println(e);   
         }
     // btnCareer method end.   
    }//GEN-LAST:event_btnCareerActionPerformed

    public void showStudentsTable(ResultSet rs)
    {
        DefaultTableModel dtm = new DefaultTableModel(); 
        String[] data = new String[3];
            
       
        
        try
        {        
            if(rs.next())
            {    
                dtm.addColumn("ID");
                dtm.addColumn("Name");
                dtm.addColumn("Lastname");
                StudentsTable.setModel(dtm);
                
                data[0] = rs.getString(1);
                data[1] = rs.getString(2);
                data[2] = rs.getString(3);
                dtm.addRow(data);
                
                while(rs.next())
                {
                  data[0] = rs.getString(1);
                  data[1] = rs.getString(2);
                  data[2] = rs.getString(3);
                  dtm.addRow(data);
                }
            }
            
            else
              JOptionPane.showMessageDialog(null , "NO DATA");
          
        }catch(SQLException e)
         {
             System.out.println(e);   
         }
        
    } // showStudentsTable end.
    
